package democlass1;

public class Demo_Class {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0;
		
		System.out.println("i before Initialization "+i);
		
		i=10;
		System.out.println("i after Assign value "+i);
		
		i++;
		System.out.println("i after Increment value i++ "+i);
		
		i=10+20;
		System.out.println("i after Addition value "+i);
		
		i--;
		System.out.println("i after Decrement value i-- "+i);

	}

}
